mapper.update:
update sys_emp_info
photoUrl = #{photoUrl}, workno = #{workno}, empName = #{empName}, gender = #{gender}, firtorgname = #{firtorgname}, teamAffiliated = #{teamAffiliated}, employType = #{employType}, firstWorkDate = #{firstWorkDate}, hireDate = #{hireDate}, mobile = #{mobile}, EXT_COL_994 = #{EXT_COL_994}, EXT_COL_951 = #{EXT_COL_951}, EXT_COL_999 = #{EXT_COL_999}, EXT_COL_974 = #{EXT_COL_974}, cascadingJobGrade = #{cascadingJobGrade}, other1 = #{other1}, other2 = #{other2}, birthDate = #{birthDate}, nation = #{nation}, idCardNo = #{idCardNo}, politics = #{politics}, EXT_COL_998 = #{EXT_COL_998}, nationality = #{nationality}, hujiAddress = #{hujiAddress}, EXT_COL_934 = #{EXT_COL_934}, hujiCity = #{hujiCity}, nowAddr = #{nowAddr}, lastEduDegree = #{lastEduDegree}, highDegree = #{highDegree}, EXT_COL_935 = #{EXT_COL_935}, marriage = #{marriage}, fertilityStatus = #{fertilityStatus}, corpEmail = #{corpEmail}, terminationDate = #{terminationDate},
where empid = #{empid}

mapper.select:
 select a.year,a.rank from sys_emp_upgrade a
INNER JOIN sys_emp_info b on a.workno=b.workno
where b.empid=#{empid}

mapper.insert:
insert into sys_emp_upgrade(year,rank,workno

)
values

    (#{item.year},#{item.rank},#{item.workno}
    )

ON DUPLICATE KEY UPDATE
rank = values(rank)

insert into sys_emp_info(empid,photoUrl, workno, empName, gender, firtorgname, teamAffiliated, employType, firstWorkDate, hireDate, mobile, EXT_COL_994, EXT_COL_951, EXT_COL_999, EXT_COL_974, cascadingJobGrade, other1, other2, birthDate, nation, idCardNo, politics, EXT_COL_998, nationality, hujiAddress, EXT_COL_934, hujiCity, nowAddr, lastEduDegree,highDegree, EXT_COL_935, marriage, fertilityStatus, corpEmail
) values
(#{item.empid},#{item.photoUrl},#{item.workno},#{item.empName},#{item.gender},#{item.firtorgname},#{item.teamAffiliated}, #{item.employType},#{item.firstWorkDate},#{item.hireDate},#{item.mobile},#{item.EXT_COL_994},#{item.EXT_COL_951}, #{item.EXT_COL_999},#{item.EXT_COL_974},#{item.cascadingJobGrade},#{item.other1},#{item.other2},#{item.birthDate}, #{item.nation},#{item.idCardNo},#{item.politics},#{item.EXT_COL_998},#{item.nationality},#{item.hujiAddress}, #{item.EXT_COL_934},#{item.hujiCity},#{item.nowAddr},#{item.lastEduDegree},#{item.highDegree},#{item.EXT_COL_935}, #{item.marriage},#{item.fertilityStatus},#{item.corpEmail}
)
ON DUPLICATE KEY UPDATE workno = values(workno), photoUrl = values(photoUrl), empName = values(empName), gender = values(gender), firtorgname = values(firtorgname), teamAffiliated = values(teamAffiliated), employType = values(employType), firstWorkDate = values(firstWorkDate), hireDate = values(hireDate), mobile = values(mobile), EXT_COL_994 = values(EXT_COL_994), EXT_COL_951 = values(EXT_COL_951), EXT_COL_999 = values(EXT_COL_999), EXT_COL_974 = values(EXT_COL_974), cascadingJobGrade = values(cascadingJobGrade), other1 = values(other1), other2 = values(other2), birthDate = values(birthDate), nation = values(nation), idCardNo = values(idCardNo), politics = values(politics), EXT_COL_998 = values(EXT_COL_998), nationality = values(nationality), hujiAddress = values(hujiAddress), EXT_COL_934 = values(EXT_COL_934), hujiCity = values(hujiCity), nowAddr = values(nowAddr), lastEduDegree = values(lastEduDegree), highDegree = values(highDegree), EXT_COL_935 = values(EXT_COL_935), marriage = values(marriage), fertilityStatus = values(fertilityStatus), corpEmail = values(corpEmail)

update.foreach:
update sys_emp_info
terminationDate = #{item.terminationDate}, EXT_COL_974='主动离职',
where workno = #{item.workno}
